// ./components/PieChart.js
import React, { useEffect, useState } from "react";
import  PolarArea from "react-chartjs-2";


const KidneyPolarArea = () => {

  const [counts,setCounts] = useState([100,200,300]);
  const [labels,setLabels] = useState(["A","B","C"]);
  

  const fetchData = async () => {
    const res = await fetch("http://localhost:5000/predict/prediction_count_per_kidney_desease");
    const data = await res.json()
    .then(data => {
    setCounts(data.map((d) => d.count));
    setLabels(data.map((d) => d._id));
    });
    console.log(counts);
    return data;
  }

  useEffect(() => {

  fetchData();
  }, []);


  const d = {
    labels: labels,
    title: 'Kidney deseases count',
    datasets: [
      {
        label: "Kidney deseases count",
        type: 'polarArea',
        backgroundColor: [
            'rgb(255, 99, 132)',
            'rgb(75, 192, 192)',
            'rgb(255, 205, 86)',
            'rgb(201, 203, 207)',
            'rgb(54, 162, 235)'
          ],
        borderColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        data: counts,
      },
    ],
  };


  return (
    <div>
      <PolarArea data={d} />
    </div>
  );
}

export default KidneyPolarArea;
