const pieChartLabels = ['Covid19', 'Kidney', 'Tuberculosis'];

const getData = async () => {
    fetch('http://localhost:5000/predict/predictions_count_per_desease')
    .then(response => response.json())
    .then(data => {
        return data;
    }).catch((err) => {
        return [300, 500, 100]
    })
}

const pieChartData = getData();

const pieChartType = 'pie';

const pieChartColors = {
    backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
    borderColor: ['#2fa7ff', '#7cdb86', '#805aff']
};

export default {
    data: {
        labels: pieChartLabels,
        type: pieChartType,
        datasets: [
            {
                data: pieChartData,
                ...pieChartColors
            }
        ]
    }
}
