const data = async function getData(){
    const res = await fetch('http://localhost:5000/query/predictions_per_month');
    const { data } = await res.json();
    return data;
  }

export default {
    color: '#336cfb',
    tooltip: {
        trigger: 'none',
        axisPointer: {
            type: 'cross'
        }
    },
    legend: {
        data: ['patients 2019']
    },
    grid: {
        left: 30,
        right: 0,
        top: 50,
        bottom: 50
    },
    xAxis: [
        {
            type: 'category',
            axisTick: {
                alignWithLabel: true
            },
            axisLine: {
                onZero: false,
                lineStyle: {
                    color: '#336cfb'
                }
            },
            axisPointer: {
                label: {
                    formatter: function (params) {
                        return (
                            'patients ' + params.value + (params.seriesData.length ? '：' + params.seriesData[0].data : '')
                        );
                    }
                }
            },
            data: Object.keys(data)
        }
    ],
    yAxis: [
        {
            type: 'value'
        }
    ],
    series: [
        {
            name: 'patients 2019',
            type: 'line',
            smooth: true,
            data: Object.values(data)
        }
    ]
};
