import { useEffect, useState } from 'react';

import { IPrediction } from '../interfaces/prediction';
import axios from 'axios';

async function getPredictions() {
  const result = await axios.get('http://localhost:5000/predict/getAll_predictions');
  return result.data as IPrediction[];
}

export function useGetPredictions(): IPrediction[] {
  const [predictions, setPredictions] = useState<IPrediction[]>([]);

  useEffect(() => {
    getPredictions().then((data) => {
        setPredictions(data);
    });
  }, []);

  return predictions;
}
