// ./components/PieChart.js
import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";


const DPieChart = () => {

  const [counts,setData] = useState([]);
  

  const fetchData = async () => {
    const res = await fetch("http://localhost:5000/predict/predictions_count_per_desease");
    const data = await res.json()
    console.log(data);
    return data;
  }

  const getData = () => {
    fetchData().then((res) => {
      res.map((index) => {
        counts.push(index.count);
      })
      console.log(counts);
      return counts
    })
  }

  const labels = ["Covid19", "Kidney", "Tuberculosis"];

  const d = {
    labels: labels,
    datasets: [
      {
        label: "My First dataset",
        type: 'pie',
        backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        borderColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        data: getData(),
      },
    ],
  };


  return (
    <div>
      <Pie data={d} />
    </div>
  );
};
export default DPieChart;
