interface IList {
    id: string,
    months: {
        month: string,
        count: number
    }
 }

const data = async function getData(){
    let res = await fetch('http://localhost:5000/query/predictions_per_month');
    let data = await res.json() as IList[];
    return data;
  }

export default {
    color: '#336cfb',
    tooltip: {
        trigger: 'none',
        axisPointer: {
            type: 'cross'
        }
    },
    legend: {
        data: [data.get('_id')]
    },
    grid: {
        left: 30,
        right: 0,
        top: 50,
        bottom: 50
    },
    xAxis: [
        {
            type: 'category',
            axisTick: {
                alignWithLabel: true
            },
            axisLine: {
                onZero: false,
                lineStyle: {
                    color: '#336cfb'
                }
            },
            axisPointer: {
                label: {
                    formatter: function (params) {
                        return (
                            'patients ' + params.value + (params.seriesData.length ? '：' + params.seriesData[0].data : '')
                        );
                    }
                }
            },
            data: [
                '2019-1',
                '2019-2',
                '2019-3',
                '2019-4',
                '2019-5',
                '2019-6',
                '2019-7',
                '2019-8',
                '2019-9',
                '2019-10',
                '2019-11',
                '2019-12'
            ]
        }
    ],
    yAxis: [
        {
            type: 'value'
        }
    ],
    series: [
        {
            name: 'patients 2019',
            type: 'line',
            smooth: true,
            data: [95, 124, 132, 143, 138, 178, 194, 211, 234, 257, 241, 226]
        }
    ]
};
