// ./components/PieChart.js
import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";


const DPieChart = () => {

  const [counts,setData] = useState([100,200,300]);
  const fetched = []
  

  const fetchData = async () => {
    const res = await fetch("http://localhost:5000/predict/predictions_count_per_desease");
    const data = await res.json()
    console.log(data);
    return data;
  }

  useEffect(() => {
    
    fetchData().then(data => data.map(data => {
      fetched.push(data.count);
    }))
    setData(fetched);
    console.log(counts)
  },[]);

  const labels = ["Covid19", "Kidney", "Tuberculosis"];

  const d = {
    labels: labels,
    datasets: [
      {
        label: "My First dataset",
        type: 'pie',
        backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        borderColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        data: counts,
      },
    ],
  };


  return (
    <div>
      <Pie data={d} />
    </div>
  );
};
export default DPieChart;
