import React, { useEffect, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, 
    CartesianGrid, Tooltip } from 'recharts';


const KidneySymptomsStackedBarChart = () => {
    const [data, setData] = useState([
        {
            name: 'Page A',
            uv: 4000,
            pv: 2400,
            amt: 2400,
        },
        {
            name: 'Page B',
            uv: 3000,
            pv: 1398,
            amt: 2210,
        },
        {
            name: 'Page C',
            uv: 2000,
            pv: 9800,
            amt: 2290,
        },
        {
            name: 'Page D',
            uv: 2780,
            pv: 3908,
            amt: 2000,
        },
        {
            name: 'Page E',
            uv: 1890,
            pv: 4800,
            amt: 2181,
        },
        {
            name: 'Page F',
            uv: 2390,
            pv: 3800,
            amt: 2500,
        },
        {
            name: 'Page G',
            uv: 3490,
            pv: 4300,
            amt: 2100,
        },
    ]);

    const fetchData = async () => {
        const res = await fetch("http://localhost:5000/query/symptoms_count_per_kidney_result");
        const data = await res.json()
        .then(data => {
        setData(data.map((d) => {
            return {
                symptom: d.symptoms._id,
                desease: d._id,
                count: d.symptoms.count
                
            }
        }))
        });
        console.log(data);
      }
    
      useEffect(() => {
    
      fetchData();
      }, []);

    return (
        <BarChart
            width={500}
            height={300}
            data={data}
            margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
            }}
        >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="pv" stackId="a" fill="#8884d8" />
            <Bar dataKey="uv" stackId="a" fill="#82ca9d" />
        </BarChart>
    );
}
export default KidneySymptomsStackedBarChart
