import { Button, message, Table, Tag } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import Modal from 'antd/lib/modal/Modal';
import Axios from 'axios';
import React, { useEffect, useState } from 'react';



const ClassificationTable = (props: any) => {
  const [state, setstate] = useState([]);
  const [loading, setloading] = useState(true);
  const [modaldata, setmodaldata] = useState([]);
  const key = 'updatable';

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = (record) => {
    console.log(record);
    setmodaldata(record);
    setIsModalVisible(true);
  };

  const handleApproval = async () => {
    alert(modaldata['key'])
    console.log(JSON.stringify({ _id: modaldata['key'], approval: 1 }));
    await fetch('http://localhost:5000/predict/update_prediction_approval', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ _id: modaldata['key'], approval: 1 }),
      credentials: "same-origin"
    })
      .then((res) => {
        
        console.log(res);
        message.loading({ content: 'Updating...', key });
        setTimeout(() => {
          message.success({ content: 'Prediction Approved Successfully!', key, duration: 2 });
        }, 1000);
      })
      .catch((err) => {
        message.error('Prediction update failed.');
        console.log(err);
      })
      .finally(() => {
        setIsModalVisible(false);
      });
  };

  const handleDissaproval = async () => {
    alert(modaldata['key'])
    console.log(JSON.stringify({ _id: modaldata['key'], approval: 1 }));
    await fetch('http://localhost:5000/predict/update_prediction_approval', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ _id: modaldata['key'], approval: 0 }),
      credentials: "same-origin"
    })
      .then((res) => {
        console.log(res);
        message.loading({ content: 'Updating...', key });
        setTimeout(() => {
          message.success({ content: 'Prediction Dissaproved Successfully!', key, duration: 2 });
        }, 1000);
      })
      .catch((err) => {
        message.error('Prediction update failed.');
        console.log(err);
      })
      .finally(() => {
        setIsModalVisible(false);
      });
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const approvalSwitch = (param) => {
    switch (param) {
      case 0:
        return 'Dissaproved';
      case 1:
        return 'Approved';
      default:
        return 'Pending';
    }
  }

  const valueSwitch = (param1, param2) => {
    switch (param1) {
      case 'Kidney':
        switch (param2) {
          case 0: return 'Diagnosed with Cyst';
          case 1: return 'Normal';
          case 2: return 'Diagnosed with Stone';
          case 3: return 'Diagnosed with Tumor';
        }
      default:
        switch (param2) {
          case 0: return 'Negative';
          case 1: return 'Positive';
        }

    }
  }


  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    await Axios.post('http://localhost:5000/predict/getAll_predictions').then(
      res => {
        console.log(res.data);
        setloading(false);
        setstate(
          res.data.map(row => ({
            key: row._id,
            patient: row.patient,
            desease: row.desease,
            image_path: row.image_path,
            symptoms: row.symptoms,
            d_report: row.d_report,
            result: row.result,
            probs: row.probs,
            approval: row.approved,
            status: row.prediction_status

          }))
        );
      }
    );
  };

  const columns: ColumnsType = [
    {
      title: 'Patient Name',
      dataIndex: 'patient',
      key: 'patient',
      render: text => <a>{text}</a>,
    },
    {
      title: 'Desease',
      dataIndex: 'desease',
      key: 'desease',
      responsive: ['md'],
    },
    {
      title: 'Result',
      dataIndex: 'result',
      key: 'result',
      responsive: ['sm'],
      render: (result) => (
        result == 0 ? "Normal" : "Diagnosed"
      )
    },
    {
      title: 'Approval',
      dataIndex: 'approval',
      key: 'approval',
      responsive: ['sm'],
      render: (approval) => (
        <Tag style={{ borderRadius: 15 }} color={approvalSwitch(approval) === 'Approved' ? '#b7ce63' : '#cec759'}>
          {approvalSwitch(approval)}
        </Tag>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      responsive: ['sm'],
      render: (status) => (
        <Tag style={{ borderRadius: 15 }} color={status === 'success' ? '#b7ce63' : '#cec759'}>
          {status}
        </Tag>
      )
    },
  ];


  return (
    <>
      <Table columns={columns}
        dataSource={state}
        onRow={(record) => ({
          onClick: () => { showModal(record) }
        })} />
      <Modal title="Diagnostic Informations"
        visible={isModalVisible}
        onCancel={handleCancel}
        width={1000}
        footer={[
          <Button key="back" onClick={handleCancel}>
            Return
          </Button>,
          <Button danger loading={loading} onClick={handleDissaproval}>
            Dissaprove
          </Button>,
          <Button type="primary" loading={loading} onClick={handleApproval}>
            Approve
          </Button>
        ]}
      >

        <div className="row">
          <div className="col-md-1">
            Patient:
          </div>
          <div className="col-md-11">
            {modaldata['patient']}
          </div>
        </div>
        <div className="row">
          <div className="col-md-1">
            Desease :
          </div>
          <div className="col-md-11">
            {modaldata['desease']}
          </div>
        </div>
        <div className="row">
          <div className="col-md-1">Result :</div>
          <div className="col-md-10">
            {valueSwitch(modaldata['desease'], modaldata['result'])}
          </div>
        </div>
        <div className="row">
          <div className="w-auto p-3">
            Probabilities : </div>
          <div className="w-auto p-3">
            <ul>
              {modaldata['probs']?.map((prob) => (
                <li>{prob}</li>
              ))}
            </ul>
          </div>
        </div>
        <div className="row">
          <div className="w-auto p-3">
            Doctors report :
          </div>
          <div className="w-auto p-3">
            {modaldata['d_report']}
          </div>
        </div>
        <div className="row">
          <div className="w-auto p-3">
            Symptoms :
          </div>
          <div className="w-auto p-3">
            <ul>
              {modaldata['symptoms']?.map((symptom, index) => (
                <li>{symptom}</li>
              ))}
            </ul>
          </div>
        </div>
        {console.log(typeof (modaldata['symptoms']))}
        <div className="row">
          <div className="col s10 text-center">
            <img width='400' src={modaldata['image_path'] as string} height='400' alt='avatar' />
          </div>
        </div>
      </Modal>



    </>)

}

export default ClassificationTable;
