import React, { useEffect, useState } from "react";
import {
    LineChart,
    XAxis,
    YAxis,
    CartesianGrid,
    Line,
    Tooltip,
    Legend
} from 'recharts';

const PredictionsMonthLinechart = () => {
    const [data, setData] = useState([])

    const fetchData = async () => {
        const res = await fetch("http://localhost:5000/query/predictions_per_month");
        const data = await res.json()
            .then(data => {
                const keys = data.slice(1).keys();
                const values = data.slice(1).values();
                setData(
                    { 
                        month: keys,
                        predictions: values
                    }
                )
            })
        console.log(data);
        return data;
    }

    useEffect(() => {

        fetchData();
    }, []);


    return (
        <LineChart width={1100} height={400} data={data}
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="month" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="predictions" stroke="#8884d8" />

        </LineChart>
    )
}

export default PredictionsMonthLinechart;
