const pieChartLabels = ['Covid19', 'Kidney', 'Tuberculosis'];

const getData = async () => {
    const response = await fetch('http://localhost:3000/api/deseases');
    const data = await response.json();
    console.log(data);
    return data;
}

const pieChartData = getData();

const pieChartType = 'pie';

const pieChartColors = {
    backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
    borderColor: ['#2fa7ff', '#7cdb86', '#805aff']
};

export default {
    data: {
        labels: pieChartLabels,
        type: pieChartType,
        datasets: [
            {
                data: pieChartData,
                ...pieChartColors
            }
        ]
    }
}
