import React, { useState } from 'react';
import {
    ResponsiveContainer,
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend
} from 'recharts';


const d = [
    {
        name: 'Covid19',
        count: 4000,
        pv: 2400,
        amt: 2400
    },
    {
        name: 'Kidney',
        count: 3000,
        pv: 1398,
        amt: 2210
    },
    {
        name: 'Tuberculosis',
        count: 2000,
        pv: 9800,
        amt: 2290
    }
];

const SimpleBarChart = () => {

    const [name, setName] = useState(['Covid19', 'Kidney', 'Tuberculosis']);
    const [uv, setUv] = useState([4000, 3000, 2000]);
    const [pv, setPv] = useState([2400, 1398, 9800]);
    const [amt, setAmt] = useState([2400, 2210, 2290]);




    return (
        <ResponsiveContainer height={300} width={'100%'}>
            <BarChart
                width={200}
                height={100}
                data={d}
                margin={{
                    top: 5
                }}>
                <CartesianGrid strokeDasharray='3 3' />
                <XAxis dataKey='name' />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey='pv' fill='#8884d8' />
                <Bar dataKey='uv' fill='#82ca9d' />
            </BarChart>
        </ResponsiveContainer>
    );
};

export default SimpleBarChart;
