import React, { useState } from 'react';
import {
    ResponsiveContainer,
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend
} from 'recharts';

const [counts,setCounts] = useState([100,200,300]);
  const [labels,setLabels] = useState(["A","B","C"]);

const d = [
    {
        name: 'Covid19',
        uv: 4000,
        pv: 2400,
        amt: 2400
    },
    {
        name: 'Kidney',
        uv: 3000,
        pv: 1398,
        amt: 2210
    },
    {
        name: 'Tuberculosis',
        uv: 2000,
        pv: 9800,
        amt: 2290
    }
];

const SimpleBarChart = () => {



    return (
        <ResponsiveContainer height={300} width={'100%'}>
            <BarChart
                width={200}
                height={100}
                data={d}
                margin={{
                    top: 5
                }}>
                <CartesianGrid strokeDasharray='3 3' />
                <XAxis dataKey='name' />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey='pv' fill='#8884d8' />
                <Bar dataKey='uv' fill='#82ca9d' />
            </BarChart>
        </ResponsiveContainer>
    );
};

export default SimpleBarChart;
