import { useFetch } from "../../../../hooks/useFetch";

const pieChartLabels = ['Covid19', 'Kidney', 'Tuberculosis'];

async function fetchData() {
    const response = await fetch('http://localhost:5000/predict/predictions_count_per_desease');
    const data = await response.json();
    return data;
}
const pieChartData = fetchData().then((data) => {
    data.map((index) => {
        return index.count;
        });
})

const pieChartType = 'pie';

const pieChartColors = {
    backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
    borderColor: ['#2fa7ff', '#7cdb86', '#805aff']
};

export default {
    data: {
        labels: pieChartLabels,
        type: pieChartType,
        datasets: [
            {
                data: pieChartData,
                ...pieChartColors
            }
        ]
    }
}
