// ./components/PieChart.js
import React, { useState } from "react";
import { Pie } from "react-chartjs-2";


const DPieChart = () => {

  const [data,setData] = useState([]);
  

  const fetchData = async () => {
    const res = await fetch("http://localhost:5000/predict/predictions_count_per_desease");
    const data = await res.json();
    console.log(data);
    return data;
  }

  const getData = fetchData().then((res) => {
    res.map((index) => {
      data.push(index.count);
    })
    console.log(data);
  }).catch((err) => {
    console.log(err);  
  })

  const labels = ["Covid19", "Kidney", "Tuberculosis"];

  const d = {
    labels: labels,
    datasets: [
      {
        label: "My First dataset",
        type: 'pie',
        backgroundColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        borderColor: ['#2fa7ff', '#7cdb86', '#805aff'],
        data: data,
      },
    ],
  };


  return (
    <div>
      <Pie data={d} />
    </div>
  );
};
export default DPieChart;
