const httpGet = async (theUrl) => {
    const response = await fetch(theUrl, {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    });
    response.json().then((data) => {
        console.log(data);
    })
  };

  const data = httpGet("http://localhost:5000/query/predictions_per_month");


export default {
    color: '#336cfb',
    tooltip: {
        trigger: 'none',
        axisPointer: {
            type: 'cross'
        }
    },
    legend: {
        data: [data.get('_id')]
    },
    grid: {
        left: 30,
        right: 0,
        top: 50,
        bottom: 50
    },
    xAxis: [
        {
            type: 'category',
            axisTick: {
                alignWithLabel: true
            },
            axisLine: {
                onZero: false,
                lineStyle: {
                    color: '#336cfb'
                }
            },
            axisPointer: {
                label: {
                    formatter: function (params) {
                        return (
                            'patients ' + params.value + (params.seriesData.length ? '：' + params.seriesData[0].data : '')
                        );
                    }
                }
            },
            data: Object.keys(data['months'] || {})
        }
    ],
    yAxis: [
        {
            type: 'value'
        }
    ],
    series: [
        {
            name: 'patients 2019',
            type: 'line',
            smooth: true,
            data: [95, 124, 132, 143, 138, 178, 194, 211, 234, 257, 241, 226]
        }
    ]
};
