import { useState } from "react";

const fetchData = () => {

    const [year,setYear] = useState();
    const [months,setMonths] = useState();


    const getData = async () => {
        await fetch("http://localhost:5000/query/predictions_per_month")
            .then(res => res.json())
            .then(data => {
            })
    };

}


export default {
    color: '#336cfb',
    tooltip: {
        trigger: 'none',
        axisPointer: {
            type: 'cross'
        }
    },
    legend: {
        data: year
    },
    grid: {
        left: 30,
        right: 0,
        top: 50,
        bottom: 50
    },
    xAxis: [
        {
            type: 'category',
            axisTick: {
                alignWithLabel: true
            },
            axisLine: {
                onZero: false,
                lineStyle: {
                    color: '#336cfb'
                }
            },
            axisPointer: {
                label: {
                    formatter: function (params) {
                        return (
                            'patients ' + params.value + (params.seriesData.length ? '：' + params.seriesData[0].data : '')
                        );
                    }
                }
            },
            data: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
        }
    ],
    yAxis: [
        {
            type: 'value'
        }
    ],
    series: [
        {
            name: 'patients ' + year,
            type: 'line',
            smooth: true,
            data: months
        }
    ]
};
